mapper.select:

	  	select *
		from(
			select rownum as rn, a.*
				from(select * from REVIEW


		    where movieTitle like '%'||#{searchName}||'%'
          	where writer like '%'||#{searchName}||'%'
	        where movieTitle like '%'||#{searchName}||'%' or writer like '%'||#{searchName}||'%' 
	        where 1 = 1


		    order by bno desc) a
		) where rn > (#{pageNum}-1) * #{amount} and rn <= #{pageNum} * #{amount}

mapper.update:
update review
set content = #{content}
where bno = #{bno}
